import React, { useRef, useState } from 'react';
import { Link } from 'react-router-dom'; // Si vous utilisez React Router pour la navigation

const CategoryCarousel = () => {
  const categories = [
    { name: 'Soins', link: '/soins', img: 'https://via.placeholder.com/200x200?text=Soins' },
    { name: 'Animaux', link: '/animaux', img: 'https://via.placeholder.com/200x200?text=Animaux' },
    { name: 'Technologie', link: '/technologie', img: 'https://via.placeholder.com/200x200?text=Technologie' },
    { name: 'Sports', link: '/sports', img: 'https://via.placeholder.com/200x200?text=Sports' },
    { name: 'Musique', link: '/musique', img: 'https://via.placeholder.com/200x200?text=Musique' },
    { name: 'Arts', link: '/arts', img: 'https://via.placeholder.com/200x200?text=Arts' },
    { name: 'Voyages', link: '/voyages', img: 'https://via.placeholder.com/200x200?text=Voyages' },
    { name: 'Mode', link: '/mode', img: 'https://via.placeholder.com/200x200?text=Mode' },
    { name: 'Cuisine', link: '/cuisine', img: 'https://via.placeholder.com/200x200?text=Cuisine' },
    { name: 'Livres', link: '/livres', img: 'https://via.placeholder.com/200x200?text=Livres' },
    { name: 'Jardinage', link: '/jardinage', img: 'https://via.placeholder.com/200x200?text=Jardinage' },
    { name: 'Bien-être', link: '/bien-etre', img: 'https://via.placeholder.com/200x200?text=Bien-être' },
  ];

  // Dupliquer les catégories pour créer un effet infini
  const allCategories = [...categories, ...categories]; // Dupliquer les catégories

  const containerRef = useRef(null); // Référence pour manipuler le défilement

  // Fonction pour faire défiler à droite
  const scrollRight = () => {
    containerRef.current.scrollBy({
      left: 220, // Défilement vers la droite (ajuster la valeur selon la largeur des cartes)
      behavior: 'smooth',
    });
  };

  // Fonction pour faire défiler à gauche
  const scrollLeft = () => {
    containerRef.current.scrollBy({
      left: -220, // Défilement vers la gauche (ajuster la valeur selon la largeur des cartes)
      behavior: 'smooth',
    });
  };

  return (
    <div className="my-5 position-relative">
      <div className="category-carousel-container-wrapper">
        {/* Flèche gauche */}
        <button className="carousel-control-prev" onClick={scrollLeft} aria-label="Précédent">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
        </button>

        {/* Le carrousel avec les catégories */}
        <div className="category-carousel-container" ref={containerRef}>
          {allCategories.map((category, index) => (
            <div className="category-card mx-2" key={index}>
              <Link to={category.link}>
                <div className="card">
                  <img src={category.img} alt={category.name} />
                  <div className="card-body text-center">
                    <h5 className="card-title">{category.name}</h5>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>

        {/* Flèche droite */}
        <button className="carousel-control-next" onClick={scrollRight} aria-label="Suivant">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
        </button>
      </div>
    </div>
  );
};

export default CategoryCarousel;
